in ningún tipo de limitaciones.', 'ASÍ NOS ORGANIZAMOS', '', 'inherit', 'open', 'open', '', '41-revision-v1', '', '', '2013-12-19 15:03:35', '2013-12-19 15:03:35', '', 41, 'http://amnistiawp.local/41-revision-v1/', 0, 'revision', '', 0),
