(1614, 637, '_wp_attached_file', '2014/12/187051_Sochi_Olympics_Action_Moscow_January_2014.jpg'),
(1615, 637, '_wp_attachment_metadata', 'a:5:{s:5:"width";i:600;s:6:"height";i:400;s:4:"file";s:60:"2014/12/187051_Sochi_Olympics_Action_Moscow_January_2014.jpg";s:5:"sizes";a:4:{s:9:"thumbnail";a:4:{s:4:"file";s:60:"187051_Sochi_Olympics_Action_Moscow_January_2014-150x150.jpg";s:5:"width";i:150;s:6:"height";i:150;s:9:"mime-type";s:10:"image/jpeg";}s:6:"medium";a:4:{s:4:"file";s:60:"187051_Sochi_Olympics_Action_Moscow_January_2014-300x200.jpg";s:5:"width";i:300;s:6:"height";i:200;s:9:"mime-type";s:10:"image/jpeg";}s:11:"thumb_actua";a:4:{s:4:"file";s:60:"187051_Sochi_Olympics_Action_Moscow_January_2014-450x200.jpg";s:5:"width";i:450;s:6:"height";i:200;s:9:"mime-type";s:10:"image/jpeg";}s:12:"thumb_banner";a:4:{s:4:"file";s:60:"187051_Sochi_Olympics_Action_Moscow_January_2014-600x314.jpg";s:5:"width";i:600;s:6:"height";i:314;s:9:"mime-type";s:10:"image/jpeg";}}s:10:"image_meta";a:11:{s:8:"aperture";d:7.0999999999999996447286321199499070644378662109375;s:6:"credit";s:37:"© Denis Bochkarev &amp; Genia Rakina";s:6:"camera";s:12:"Canon EOS 6D";s:7:"caption";s:1108:"Ballerina dances in a “one-person protest” against repression of free speech in Russia. Central Moscow, 30th January 2014.\r\n\r\nPrior to the creative stunt, a 200,000 strong global petition was delivered to the offices of Russian President Vladimir Putin, by Amnesty International European directors, calling on him to repeal a series of repressive laws restricting the right to freedom of expression, assembly and association in the run up to the Sochi Winter Olympics. At the same time, hundreds of Amnesty International members and supporters organized actions and delivered petitions to the Russian embassies in their own countries. \r\n\r\n“The message to President Putin, from rank and file Amnesty International supporters to world leaders staying away from the Games, is the same – repeal series of laws that restrict the rights to freedom of expression, association and assembly. \r\n“These laws suppress creativity and the development of civil society and undermine the legitimate role of human rights activists in Russia,” said Sergei Nikitin, Amnesty International’s Moscow Office Director.";s:17:"created_timestamp";i:1391087873;s:9:"copyright";s:37:"© Denis Bochkarev &amp; Genia Rakina";s:12:"focal_length";s:2:"46";s:3:"iso";s:4:"1600";s:13:"shutter_speed";s:6:"0.0005";s:5:"title";s:41:"Sochi Olympics Action Moscow January 2014";s:11:"orientation";i:1;}}'),
(1616, 638, '_wp_attached_file', '2014/12/187528_Global_Day_of_Action_against_Uganda_s_Anti-Homosexuality_Bill__1_.jpg'),
(1617, 638, '_wp_attachment_metadata', 'a:5:{s:5:"width";i:579;s:6:"height";i:600;s:4:"file";s:84:"2014/12/187528_Global_Day_of_Action_against_Uganda_s_Anti-Homosexuality_Bill__1_.jpg";s:5:"sizes";a:4:{s:9:"thumbnail";a:4:{s:4:"file";s:84:"187528_Global_Day_of_Action_against_Uganda_s_Anti-Homosexuality_Bill__1_-150x150.jpg";s:5:"width";i:150;s:6:"height";i:150;s:9:"mime-type";s:10:"image/jpeg";}s:6:"medium";a:4:{s:4:"file";s:84:"187528_Global_Day_of_Action_against_Uganda_s_Anti-Homosexuality_Bill__1_-289x300.jpg";s:5:"width";i:289;s:6:"height";i:300;s:9:"mime-type";s:10:"image/jpeg";}s:11:"thumb_actua";a:4:{s:4:"file";s:84:"187528_Global_Day_of_Action_against_Uganda_s_Anti-Homosexuality_Bill__1_-450x200.jpg";s:5:"width";i:450;s:6:"height";i:200;s:9:"mime-type";s:10:"image/jpeg";}s:12:"thumb_banner";a:4:{s:4:"file";s:84:"187528_Global_Day_of_Action_against_Uganda_s_Anti-Homosexuality_Bill__1_-579x314.jpg";s:5:"width";i:579;s:6:"height";i:314;s:9:"mime-type";s:10:"image/jpeg";}}s:10:"image_meta";a:11:{s:8:"aperture";i:0;s:6:"credit";s:36:"Azmina Dhrodia/Amnesty International";s:6:"camera";s:0:"";s:7:"caption";s:489:"Thousands of civil society activists, including Amnesty International supporters in the UK, Canada, Spain and Germany are acting together today in solidarity with campaigners in Uganda to show their opposition to Uganda’s Anti-Homosexuality Bill and call on President Museveni to veto it.  \r\n\r\nPictured here are staff from the International Secretariat in London, as they hand over 86,169 signatures to the Ugandan High Commission, calling for the Anti-Homosexuality Bill to be scrapped.";s:17:"created_timestamp";i:0;s:9:"copyright";s:36:"Azmina Dhrodia/Amnesty International";s:12:"focal_length";i:0;s:3:"iso";i:0;s:13:"shutter_speed";i:0;s:5:"title";s:63:"Global Day of Action against Uganda’s Anti-Homosexuality Bill";s:11:"orientation";i:0;}}'),
