(1651, 655, '_wp_attachment_metadata', 'a:5:{s:5:"width";i:600;s:6:"height";i:400;s:4:"file";s:69:"2014/12/201609_Malala_Ambassador_of_Conscience_Award_Dublin_2013_.jpg";s:5:"sizes";a:4:{s:9:"thumbnail";a:4:{s:4:"file";s:69:"201609_Malala_Ambassador_of_Conscience_Award_Dublin_2013_-150x150.jpg";s:5:"width";i:150;s:6:"height";i:150;s:9:"mime-type";s:10:"image/jpeg";}s:6:"medium";a:4:{s:4:"file";s:69:"201609_Malala_Ambassador_of_Conscience_Award_Dublin_2013_-300x200.jpg";s:5:"width";i:300;s:6:"height";i:200;s:9:"mime-type";s:10:"image/jpeg";}s:11:"thumb_actua";a:4:{s:4:"file";s:69:"201609_Malala_Ambassador_of_Conscience_Award_Dublin_2013_-450x200.jpg";s:5:"width";i:450;s:6:"height";i:200;s:9:"mime-type";s:10:"image/jpeg";}s:12:"thumb_banner";a:4:{s:4:"file";s:69:"201609_Malala_Ambassador_of_Conscience_Award_Dublin_2013_-600x314.jpg";s:5:"width";i:600;s:6:"height";i:314;s:9:"mime-type";s:10:"image/jpeg";}}s:10:"image_meta";a:11:{s:8:"aperture";d:5.5999999999999996447286321199499070644378662109375;s:6:"credit";s:21:"Amnesty International";s:6:"camera";s:20:"Canon EOS 5D Mark II";s:7:"caption";s:587:"Malala Yousafzai - speaking at Ambassador of Conscience Award Ceremony Dublin September  2014 \r\n\r\nMalala Yousafzai, the schoolgirl shot and almost killed by the Taliban for campaigning for girls’ education, presented with Amnesty International’s Ambassador of Conscience award.\r\n\r\nReceiving the Amnesty International Ambassador of Conscience award in Dublin, Ireland the 16-year-old said: “I want to live in a world where free, compulsory education is available everywhere to every child.”\r\n\r\nBono, who handed over the award, spoke of her “once in a century kind of courage”.";s:17:"created_timestamp";i:1379445039;s:9:"copyright";s:21:"Amnesty International";s:12:"focal_length";s:3:"400";s:3:"iso";s:4:"2500";s:13:"shutter_speed";s:6:"0.0125";s:5:"title";s:49:"Malala Ambassador of Conscience Award Dublin 2013";s:11:"orientation";i:0;}}'),
