(2092, 782, '_wp_attachment_metadata', 'a:5:{s:5:"width";i:241;s:6:"height";i:350;s:4:"file";s:17:"2014/11/Peter.jpg";s:5:"sizes";a:4:{s:9:"thumbnail";a:4:{s:4:"file";s:17:"Peter-150x150.jpg";s:5:"width";i:150;s:6:"height";i:150;s:9:"mime-type";s:10:"image/jpeg";}s:6:"medium";a:4:{s:4:"file";s:17:"Peter-207x300.jpg";s:5:"width";i:207;s:6:"height";i:300;s:9:"mime-type";s:10:"image/jpeg";}s:11:"thumb_actua";a:4:{s:4:"file";s:17:"Peter-241x200.jpg";s:5:"width";i:241;s:6:"height";i:200;s:9:"mime-type";s:10:"image/jpeg";}s:12:"thumb_banner";a:4:{s:4:"file";s:17:"Peter-241x314.jpg";s:5:"width";i:241;s:6:"height";i:314;s:9:"mime-type";s:10:"image/jpeg";}}s:10:"image_meta";a:11:{s:8:"aperture";i:0;s:6:"credit";s:0:"";s:6:"camera";s:0:"";s:7:"caption";s:0:"";s:17:"created_timestamp";i:0;s:9:"copyright";s:0:"";s:12:"focal_length";i:0;s:3:"iso";i:0;s:13:"shutter_speed";i:0;s:5:"title";s:0:"";s:11:"orientation";i:0;}}'),
(2093, 783, '_wp_attached_file', '2015/02/22.jpg'),
(2094, 783, '_wp_attachment_metadata', 'a:5:{s:5:"width";i:166;s:6:"height";i:300;s:4:"file";s:14:"2015/02/22.jpg";s:5:"sizes";a:3:{s:9:"thumbnail";a:4:{s:4:"file";s:14:"22-150x150.jpg";s:5:"width";i:150;s:6:"height";i:150;s:9:"mime-type";s:10:"image/jpeg";}s:6:"medium";a:4:{s:4:"file";s:14:"22-166x300.jpg";s:5:"width";i:166;s:6:"height";i:300;s:9:"mime-type";s:10:"image/jpeg";}s:11:"thumb_actua";a:4:{s:4:"file";s:14:"22-166x200.jpg";s:5:"width";i:166;s:6:"height";i:200;s:9:"mime-type";s:10:"image/jpeg";}}s:10:"image_meta";a:11:{s:8:"aperture";i:0;s:6:"credit";s:10:"© Private";s:6:"camera";s:0:"";s:7:"caption";s:1025:"Raif Badawi a Saudi website founder and blogger, undated photo.\r\n\r\nRaif Badawi, founder of a website for political and social debate, “Saudi Arabian Liberals”, has been detained since 17 June 2012 in a prison in Briman, in Jeddah. \r\nHe was charged with “setting up a website that undermines general security” and ridiculing Islamic religious figures. His trial began in June 2012 in the District Court in Jeddah, and was marred by irregularities. \r\n\r\nThe charges against Raif Badawi relate to a number of articles he has written, including one about Valentine’s Day – the celebration of which is prohibited in Saudi Arabia. He was accused of ridiculing Saudi Arabia’s Commission on the Promotion of Virtue and Prevention of Vice (also known as the religious police) in the conclusion of his article. \r\nThe charges against him also mention his failure to remove articles by other people on his website, including one insinuating that the al-Imam Mohamed ibn Saud University had become “a den for terrorists”.";s:17:"created_timestamp";i:0;s:9:"copyright";s:10:"© Private";s:12:"focal_length";i:0;s:3:"iso";i:0;s:13:"shutter_speed";i:0;s:5:"title";s:11:"Raif Badawi";s:11:"orientation";i:1;}}'),
